import React, {Component} from 'react';
import TodoList from '../todo-list/todo-list';
import ItemAddForm from '../item-add-form';
import SearchForm from '../search-form';
const ligbox = <span>Login please</span>;
const login = false;

const Hed = () => {
    return (
      <h1>My to do List</h1>
    );
  };
export default class App extends Component{
    maxId = 100;
    createToDoItem(label){
        return {
            label: label,
            important: false,
            id: this.maxId++,
            done: false
        }
    }
    state = {
        todoData : [
            this.createToDoItem('Drink coffee'),
            this.createToDoItem('Write code'),
            this.createToDoItem('Sleep')
        ]
    };
    deleteItem = (id) => {
        this.setState (({todoData}) => {
            const idx = todoData.findIndex((el) => el.id === id);
            const newArray = [...todoData.slice(0,idx), ...todoData.slice(idx+1)];
            return { todoData : newArray};
        });
    };
    addItem = (text) => {
        console.log('added: ',text);
        const newItem = this.createToDoItem(text);
        this.setState(({todoData}) => {
            const newAr = [
                ...todoData,
                newItem
            ]
            return {todoData : newAr};
        });
        
    };
    stayItem = (text) => {
        console.log('search: ',text);
        this.setState(({todoData}) => {
        todoData.forEach(function(item, index, array) {
            if (!item.label.includes(text)){
                console.log('it: ',item, index);
                const idx = index;
                const newArray = [...todoData.slice(0,idx), ...todoData.slice(idx+1)];
                return { todoData : newArray};
            };
          });
        });
    };
    onToggleImportant = (id) =>{
        this.setState(({todoData})=>{
            const idx = todoData.findIndex((el) => el.id === id);
            const oldItem = todoData[idx];
            const newItem = {...oldItem, important: !oldItem.important};
            const newArray = [...todoData.slice(0,idx),newItem, ...todoData.slice(idx+1)];
            return { todoData : newArray};
        });
    };
    onToggleDone = (id) =>{
        this.setState(({todoData})=>{
            const idx = todoData.findIndex((el) => el.id === id);
            const oldItem = todoData[idx];
            const newItem = {...oldItem, done: !oldItem.done};
            const newArray = [...todoData.slice(0,idx),newItem, ...todoData.slice(idx+1)];
            return { todoData : newArray};
        });
    };
    
    render(){
    return (
      <div>
        {login ? null : ligbox }
        <Hed />
        <SearchForm onStayItems={this.stayItem}/>
        <TodoList todos = {this.state.todoData} 
        onToggleDone={this.onToggleDone}
        onToggleImportant={this.onToggleImportant}
        onDeleted={this.deleteItem} />
        <ItemAddForm onItemAdded={this.addItem}/>
      </div>
    );
    }
  };
