import React, {Component} from 'react';
import './search-form.css';

export default class SearchForm extends Component{
    state = {
        term: ''
    };

    onLabelChange = (e)=>{
        this.setState({
            label: e.target.value
        });
    };
    onSubmit = (e)=>{
        e.preventDefault();
        this.props.onStayItems(this.state.label);
        this.setState({
            label: ''
        });
    };
    
    render(){
        const searchStyle = {
            fontSize: '25px'
        };
        return(
            <form className='search-formt d-flex' onSubmit={this.onSubmit}>
                <input style = {searchStyle} 
                onChange={this.onLabelChange}
                value={this.state.term}
                placeholder = "search" />
                <button className='btn btn-outline-secondary'>
                    All
                </button>
                <button className='btn btn-outline-secondary'>
                    Active
                </button>
                <button className='btn btn-outline-secondary'>
                    Done
                </button>
            </form>
        )      
        
    }
}
